<?php/** * Admin specific service layer * */class Weblynx_DataMappers_Admin extends Weblynx_DataMappers_Abstract {        /* News Related Functions */    public function getNewsItems() {        $sql = "SELECT * FROM news ORDER BY newsdate DESC";                return $this->db->fetchAll($sql);    }        /* Header image functions */    public function getHeaderImageById($header_id) {        $sql = "SELECT * FROM header_images WHERE header_id = " . $header_id;                return $this->db->fetchRow($sql);    }        public function getHeaderByOrderNumber($orderNum) {        $sql = "SELECT * FROM header_images WHERE ordering = " . $orderNum;                return $this->db->fetchRow($sql);    }        /* Editable regions stuff */    public function getEditableRegions() {        $sql = "SELECT * FROM editable_regions ORDER BY slug ASC";                return $this->db->fetchAll($sql);    }        /* Form stuff */    public function getInitialEnquiries() {        $sql = "SELECT * FROM initial_enquiry WHERE approved = 'N' ORDER BY application_date DESC";                return $this->db->fetchAll($sql);    }    }